tmin=0; tmax = 4; dt = 0.01;t = tmin:dt:tmax;x1 = 1.*(t>=0 & t<=1);xa = 1;xb = -1;x2 = xa.*(t>=0 & t<=1) + xb.*(t>=1 & t<=2);x3 = conv(x1,x2);n3 = length(x3);t1 = 0:1:n3-1;subplot(3,1,1); plot(t,x1);xlabel('t'); ylabel('x2(t)'); title('signal x1(t)');subplot(3,1,2); plot(t,x2);xlabel('t'); ylabel('x2(t)'); title('signal x2(t)');subplot(3,1,3); plot(t1,x3);xlabel('t / dt'); ylabel('x3(t) / dt');title('single, x3(t) = x1(t)* x2(t)');