T = 2;tmin = 0; tmax = 2*T; dt = 0.01;t = tmin:dt:tmax;x1 = exp(-0.7*t).*(t>=0 & t<T);x2 = 1.*(t>=0 & t<T);x3 = conv(x1,x2);n3 = length(x3);t1 = 0:1:n3-1;subplot(3,2,1); plot(t,x1); title('signal x1(t)');%xlabel('t'); ylable('x1(t)');subplot(3,2,2); plot(t,x2); title('signal x2(t)');xlabel('t'); ylabel('x2(t)');subplot(3,2,3:4); plot(t1,x3); title('signal, x3(t) = x1(t)*x2(t)');xlabel('t/dt');  ylabel('x3(t) / dt');x1d = deconv(x3,x2);x2d = deconv(x3,x1);ymin=min([min(x1d), min(x2d)]);ymax = max([max(x1d), max(x2d)]);subplot(3,2,5); plot(t,x1d); axis([tmin tmax ymin ymax]);xlabel('t'); ylabel('x1(t)'); title('extracted signal, x1(t)');subplot(3,2,6); plot(t,x2d); axis([tmin tmax ymin ymax]);xlabel('t'); ylabel('x2(t)'); title('extracted signal, x2(t)');