% x(t);t = 0:0.01:5;x = t.*cos(2*pi*t);subplot(3,2,1); plot (t,x);legend('x(t)');% x(-t)subplot(3,2,2); plot(-t,x);legend('x(-t)');% x(t/5)subplot(3,2,3); plot (t/5,x);legend('x(t/5)');%x(1+3t)subplot(3,2,4); plot(1+3*t,x);legend('x(1+3t)');%x(-1-3t)subplot(3,2,5); plot(-1-3*t,x);legend('x(-1-3t)');% x(t);t = 0:0.01:5;x = t.*cos(2*pi*t);subplot(3,2,1); plot (t,x);legend('x(t)');% x(-t)subplot(3,2,2); plot(-t,x);legend('x(-t)');% x(t/5)subplot(3,2,3); plot (t/5,x);legend('x(t/5)');%x(1+3t)subplot(3,2,4); plot(1+3*t,x);legend('x(1+3t)');%x(-1-3t)subplot(3,2,5); plot(-1-3*t,x);legend('x(-1-3t)');% x(t);t = 0:0.01:5;x = t.*cos(2*pi*t);subplot(3,2,1); plot (t,x);legend('x(t)');% x(-t)subplot(3,2,2); plot(-t,x);legend('x(-t)');% x(t/5)subplot(3,2,3); plot (t/5,x);legend('x(t/5)');%x(1+3t)subplot(3,2,4); plot(1+3*t,x);legend('x(1+3t)');%x(-1-3t)subplot(3,2,5); plot(-1-3*t,x);legend('x(-1-3t)');% x(t);t = 0:0.01:5;x = t.*cos(2*pi*t);subplot(3,2,1); plot (t,x);legend('x(t)');% x(-t)subplot(3,2,2); plot(-t,x);legend('x(-t)');% x(t/5)subplot(3,2,3); plot (t/5,x);legend('x(t/5)');%x(1+3t)subplot(3,2,4); plot(1+3*t,x);legend('x(1+3t)');%x(-1-3t)subplot(3,2,5); plot(-1-3*t,x);legend('x(-1-3t)');% x(t);t = 0:0.01:5;x = t.*cos(2*pi*t);subplot(3,2,1); plot (t,x);legend('x(t)');% x(-t)subplot(3,2,2); plot(-t,x);legend('x(-t)');% x(t/5)subplot(3,2,3); plot (t/5,x);legend('x(t/5)');%x(1+3t)subplot(3,2,4); plot(1+3*t,x);legend('x(1+3t)');%x(-1-3t)subplot(3,2,5); plot(-1-3*t,x);legend('x(-1-3t)');% x(t);t = 0:0.01:5;x = t.*cos(2*pi*t);subplot(3,2,1); plot (t,x);legend('x(t)');% x(-t)subplot(3,2,2); plot(-t,x);legend('x(-t)');% x(t/5)subplot(3,2,3); plot (t/5,x);legend('x(t/5)');%x(1+3t)subplot(3,2,4); plot(1+3*t,x);legend('x(1+3t)');%x(-1-3t)subplot(3,2,5); plot(-1-3*t,x);legend('x(-1-3t)');% x(t);t = 0:0.01:5;x = t.*cos(2*pi*t);subplot(3,2,1); plot (t,x)