clear all syms t real syms a real syms s complex   %( a) real t; x = t ;%t = x; disp('(a) Laplace transform of "t" is'); laplacce (x)  %(b) x=exp(-a*t); disp('(b) Laplace transform of "exp(-a*t)" is'); laplace(x) %(c) x=t*exp(-a*t); disp('(c) Laplace transform of t*exp(-a*t) is'); laplace(x) %(d) sg=real(s); %s - complex frequency ; sg - sigma ; o-omega o=imag(s); s=sg+(i*o); x=cos(o*t); disp('(d) Laplace transform of "cos(o*t)" is'); laplace(x) %(e) x=sinh(o*t); disp('(e) Laplace transform of  "sinh(o*t)" is'); laplace(x) %(f) x = exp (- a * t ) * sin ( o * t ) ; disp('(f) Laplace transform of "exp(-a*t)*sin(o*t)" is'); laplacce(x)