tmin = 0;  tmax = 10;  dt = 0.01;t = tmin : dt : tmax;x1 = 1.*(t >= 1 & t <= 10);x2 = 1.*(t >= 2 & t <=10 );x3 = conv(x1,x2);n3 = length(x3);t1 = 0:1:n3-1;subplot(3,1,1);  plot(t,x1);xlabel('t'); ylabel('x1(t)');title('signal x1(t)');subplot(3,1,2); plot(t,x2);xlabel('t'); ylabel('x2(t)');title('signal x2(t)');subplot(3,1,3); plot(t1,x3); xlim([0,600]);xlabel('t/dt'); ylabel('x3(t)/dt');title('signal x3(t) = x1(t)*x2(t)');